    private function getNodeWithTranslation($lang, $title, $slug, $nodeId = null)
    {
        $node = new Node();
        if (!is_null($nodeId)) {
            $node->setId($nodeId);
        }
        $nodeTranslation = new NodeTranslation();
        $nodeTranslation->setLang($lang)
            ->setTitle($title)
            ->setSlug($slug);
        $node->addNodeTranslation($nodeTranslation);

        return $node;
    }
